import React, { UseState, Component, useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { postUserData } from '../../../store/actionCreators'

import fb from '../../../assets/modalHandles/fb.png'
import sc from '../../../assets/modalHandles/add-sc.png'
import yt from '../../../assets/modalHandles/add-yt.png'
import pencil from '../../../assets/edit-pencil.png'
import ig from '../../../assets/handles/ig.png'
import undo from '../../../assets/undo.png'
import './AddHandles.css'

const AddHandles = (props) => {

    const [input, setInput] = useState({
        fb: '',
        ig: '',
        sc: '',
        yt: ''
    })
    const [backup, setBackup] = useState({
        fb: '',
        ig: '',
        sc: '',
        yt: ''
    })


    useEffect(() => {
        setInput({
            fb: props.handles.facebook,
            ig: props.handles.instagram,
            sc: props.handles.soundcloud,
            yt: props.handles.youtube
        })
        setBackup({
            fb: props.handles.facebook,
            ig: props.handles.instagram,
            sc: props.handles.soundcloud,
            yt: props.handles.youtube
        })
        return () => {
        };
    }, [])



    const updateHandles = async () => {

        try {
            await props.updateProfile(props.uid, {
                handles: {
                    facebook: input.fb,
                    instagram: input.ig,
                    soundcloud: input.sc,
                    youtube: input.yt
                }
            })
            setBackup({
                fb: input.fb,
                ig: input.ig,
                sc: input.sc,
                yt: input.yt
            })
        }
        catch (err) {
            console.log(err)
        }

    }

    const undo = (handle) => {

        switch (handle) {
            case 'fb':
                return setFb_input(backup_fb)
            case 'ig':
                return setIg_input(backup_ig)
            case 'sc':
                return setSc_input(backup_sc)
            case 'yt':
                return setYt_input(backup_yt)
            default:
                break
        }
    }

    const hasStagedItems = () => {
        if ((fb_input !== props.handles.facebook)
            || (ig_input !== props.handles.instagram)
            || (sc_input !== props.handles.soundcloud)
            || (yt_input !== props.handles.youtube)) {
            return true
        }
        return false
    }



    return (
        <div>
            <div className="ul-img-backdrop" onClick={() => props.toggleUploadHandles(false)}></div>
            <div className="add-handles-modal">
                <div id="closeUploadImage" onClick={() => props.toggleUploadHandles(false)}><i className="fa fa-close"></i></div>
                <h1 id="handles-header">My Media</h1>

                <div className={`handle-wrapper `}>
                    <div className="icon-holder">
                        <img alt="" id="add-fb" src={fb} />
                    </div>
                    <input
                        className={`handle-input ${fb_input !== props.handles.facebook ? 'staged' : null}`}
                        spellcheck="false"
                        value={fb_input}
                        onChange={(event) => setFb_input(event.target.value)}
                        placeholder={'-insert link-'}></input>
                    <img alt="" id="pencil" src={pencil}></img>
                    <img alt="" className={`${fb_input !== props.handles.facebook ? 'undo-staged' : 'undo'}`} src={undo} onClick={() => this.undo('fb')}></img>
                </div>

                <div className="handle-wrapper">
                    <div className="icon-holder">
                        <img alt="" id="add-ig" src={ig} />
                    </div>
                    <input
                        className={`handle-input ${ig_input !== props.handles.instagram ? 'staged' : null}`}
                        spellcheck="false"
                        value={ig_input}
                        onChange={(event) => setIg_input(event.target.value)}
                        placeholder={'-insert handle-'}></input>
                    <img alt="" id="pencil" src={pencil}></img>
                    <img alt="" className={`${ig_input !== props.handles.instagram ? 'undo-staged' : 'undo'}`} src={undo} onClick={() => this.undo('ig')}></img>
                </div>

                <div className="handle-wrapper">
                    <div className="icon-holder">
                        <img alt="" id="add-sc" src={sc} />
                    </div>
                    <input
                        className={`handle-input ${sc_input !== props.handles.soundcloud ? 'staged' : null}`}
                        spellcheck="false"
                        value={sc_input}
                        onChange={(event) => setSc_input(event.target.value)}
                        placeholder={'-insert link-'}></input>
                    <img alt="" id="pencil" src={pencil}></img>
                    <img alt="" className={`${sc_input !== props.handles.soundcloud ? 'undo-staged' : 'undo'}`} src={undo} onClick={() => this.undo('sc')}></img>

                </div>

                <div className="handle-wrapper">
                    <div className="icon-holder">
                        <img alt="" id="add-yt" src={yt} />
                    </div>
                    <input
                        className={`handle-input ${yt_input !== props.handles.youtube ? 'staged' : null}`}
                        spellcheck="false"
                        value={yt_input}
                        onChange={(event) => setYt_input(event.target.value)}
                        placeholder={'-insert link-'}></input>
                    <img alt="" id="pencil" src={pencil}></img>
                    <img alt="" className={`${yt_input !== props.handles.youtube ? 'undo-staged' : 'undo'}`} src={undo} onClick={() => this.undo('yt')}></img>

                </div>
                <div className={hasStagedItems() ? 'button-staged' : 'confirm-handles'} onClick={updateHandles()}>Update</div>

            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        uid: state.uid,
        handles: state.handles
    }
}

const mapDispatchToProps = dispatch => {
    return {
        updateProfile: (uid, data) => dispatch(postUserData(uid, data).bind(this))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AddHandles)
