import React, { useState, useEffect } from 'react'
import firebase from 'firebase'
import 'firebase/firestore'
import { connect } from 'react-redux'
import FireApi from '../../../FireApi/FireApi'
import ManyPost from '../../../containers/Judge/JudgeBarz/ManyView/ManyPost/ManyPost'

const PostEditor = (props) => {

    const [postComments, setPostComments] = useState([])
    const [postVotes, setPostVotes] = useState([])
    const [post, setPost] = useState(null)

    useEffect(() => {

        FireApi.fetchSinglePost(setPost, props.pid)
        const commentsListener = FireApi.submissionCommentsForPostListener(setPostComments, props.pid)
        const votesListener = FireApi.submissionCommentsForPostListener(setPostVotes, props.pid)

        return () => {
            commentsListener()
            votesListener()
        };
    }, [props.pid])

    var content = null
    if (post) {
        content = (
            <div>
                <ManyPost
                    comments={postComments}
                    selectPost={() => null}
                    votes={postVotes}
                    {...post} />
            </div>
        )
    }
    return content
}

const mapStateToProps = state => {
    return {

    }
}

const mapDispatchToProps = dispatch => {
    return {

    }
}


export default connect(mapStateToProps, mapDispatchToProps)(PostEditor)
