import React, { Component } from 'react'
import { connect } from 'react-redux'
import firebase from 'firebase'
import 'firebase/firestore'


import './PostLikes.css'

class PostLikes extends Component {
    static defaultProps = {
        pid: ''
    }
    state = {
        points: 0,
        likes: 0,
        dislikes: 0,
        myVote: 0,
        votesListener: null
    }

    componentDidUpdate = (prevProps, prevState) => {
        if (prevProps.viewedPost !== this.props.viewedPost) {
            if (this.state.votesListener) {
                this.state.votesListener()
            }
            this.fetchInfo()
        }
    }
    componentWillUnmount = () => {
        if (this.state.votesListener) {
            this.state.votesListener()
        }
    }
    fetchInfo = () => {
        const db = firebase.firestore()
        var votesListener = db.collection('postVotes').where('pid', '==', this.props.viewedPost.pid).onSnapshot(snapshot => {
            var points = 0
            var likes = 0
            var dislikes = 0
            // for every vote of this post
            snapshot.docs.forEach(vote => {
                vote = vote.data()
                if (vote.value === 1) {
                    likes += 1
                }
                if (vote.value === -1) {
                    dislikes += 1
                }
            })
            points = (likes - dislikes) < 0 ? 0 : (likes - dislikes)
            this.setState({
                ...this.state,
                points: points,
                likes: likes,
                dislikes: dislikes
            })
        })
        this.setState({
            ...this.state,
            votesListener: votesListener
        })
    }

    render() {
        return (
            <div className='likes'>
                <div className='total-score'>
                    {this.state.points} total pt{this.state.points === 1 ? null : 's'}.
                </div>
                <div className='fans'>
                    {this.state.likes} {this.state.likes === 1 ? 'like' : 'likes'}
                </div>

            </div>
        )
    }
}

const mapStatetoProps = state => {
    return {
        uid: state.uid
    }
}

export default connect(mapStatetoProps, null)(PostLikes)
