import firebase from 'firebase'

const fetchPosts = (setter) => {
    var db = firebase.firestore()
    const listener = db.collection('submissions').onSnapshot(snapshot => {
        const fetchedPosts = []
        snapshot.forEach(doc => {
            fetchedPosts.push({
                ...doc.data(),
                pid: doc.id
            })
        })
        setter(fetchedPosts)
    })
    return listener
}

export default {
    allPostsListener: fetchPosts
}
