import React, { useState, useEffect } from 'react'
import firebase from 'firebase'
import {connect} from 'react-redux'
import './FollowBox.css'

function FollowBox(props) {

    // follows
    const [followedBy, setFollowedBy] = useState([])
    const [following, setFollowing] = useState([])
    // users
    const [users, setUsers] = useState({})
    //ui
    const [focusOn, setFocusOn] = useState('')


    useEffect(() => {
        document.addEventListener('click', toggleFocus)
        return () => {
        };
    }, [])

    useEffect(() => {
        if (props.uid) {
            fetchUsers()
            fetchFollows()
        }
    }, [props.uid])

    const toggleFocus = (event) => {
        if (event.target.classList.contains('header__section')) {
            setFocusOn('')
        }
    }

    const fetchFollows = async () => {
        var db = firebase.firestore()
        db.collection('follows').get()
            .then(snap => {
                var following = []
                var followedBy = []
                snap.forEach(doc => {
                    var follow = {
                        ...doc.data()
                    }
                    if (follow.to === props.uid) {
                        followedBy.push(follow.from)
                    } else if (follow.from === props.uid) {
                        following.push(follow.to)
                    }
                })
                setFollowedBy(followedBy)
                setFollowing(following)
            })
    }
    const fetchUsers = async () => {
        var db = firebase.firestore()
        db.collection('users').get()
            .then(snap => {
                var users = {}
                snap.forEach(doc => {
                    users[doc.id] = doc.data()
                })
                setUsers(users)
            })
    }

    var expandedStyle;
    if (focusOn) {
        expandedStyle = {
            height: '13em'
        }
    }

    return (
        <div className='follow-box'>
            <div className='follow-box__header'>
                <div className='header__section' onClick={() => setFocusOn('followers')}>
                    {followedBy.length} {'follower' + (followedBy.length !== 1 ? 's' : '')}
                </div>
                <div className='header__section' onClick={() => setFocusOn('following')}>
                    {following.length} following
                </div>
            </div>
            <div className='follow-box_body' style={expandedStyle}>

            </div>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        uid: state.uid,
    }
}

const mapDispatchToProps = dispatch => {
    return {

    }
}


export default connect(mapStateToProps, mapDispatchToProps)(FollowBox)
