import React, { useEffect, useState } from 'react'
import './ProfileBox.css'
import PhotoContainer from '../Profile/PhotoContainer/PhotoContainer'
import firebase from 'firebase'

function ProfileBox() {

    const [imgRef, setImgRef] = useState('')

    useEffect(() => {
        var storage = firebase.storage();
        storage.ref(this.props.photoRef).getDownloadURL().then(url => {
            setImgRef(url)
        }).catch(function (error) {console.log("error in Profile.js: ", error)});
        return () => {
            // cleanup
        };
    }, [])
    return (
        <div className='profile-box'>
            <div className='profile-box__block-one'>
                <PhotoContainer />
            </div>
            <div className='profile-box__block-two'></div>
            <div className='profile-box__block-three'></div>

        </div>
    )
}

export default ProfileBox
