import React, { Component } from 'react'
import './ScribbleNavBar.css'
import WordNavItem from '../../Wordsmiths/WordNavBar/WordNavItem/WordNavItem'

class ScribbleNavBar extends Component {

    componentDidMount() {
        document.addEventListener('click', this.toggleDropdown)

    }
    componentWillUnmount() {
        document.removeEventListener('click', this.toggleDropdown)
    }

    toggleDropdown(event) {
        const feedDrop = document.getElementById("feed");
        event.target.classList.contains('feed') ? feedDrop.classList.toggle('show') : feedDrop.classList.remove('show');
    }



    render() {

        return (
            <div className="ScribbleNavBar">
                <div className="ScribbleNavBarSectionContainer">
                    <div className="SortContainer">
                        <p id="sort">Settings:</p>
                        <WordNavItem
                            dropItems={['4 Barz', '8 Barz']}
                            type="feed"
                            display={this.props.feed}
                            sortAndFilter={this.props.sortAndFilter} />
                        <WordNavItem
                            dropItems={['Rock Salt', 'Permanent Marker']}
                            type="feed"
                            display={this.props.feed}
                            sortAndFilter={this.props.sortAndFilter} />
                    </div>
                </div>
            </div>
        )
    }

}

export default ScribbleNavBar;
