import React, { useEffect, useState } from 'react'
import './ProfileBox.css'
import PhotoContainer from '../Profile/PhotoContainer/PhotoContainer'
import firebase from 'firebase'
import { connect } from 'react-redux'

function ProfileBox(props) {

    const [imgURL, setImgURL] = useState('')


    // fetch imgURL if new photoRef is given
    useEffect(() => {
        if (props.photoRef) {
            console.log(props.photoRef)
            var storage = firebase.storage();
            storage.ref(props.photoRef).getDownloadURL().then(url => {
                setImgURL(url)
            }).catch(function (error) { console.log("error in Profile.js: ", error) });
            return () => {
                // cleanup
            };
        }

    }, [props.photoRef])


    return (
        <div className='profile-box'>
            <div className='profile-box__block-one'>
                <PhotoContainer imgURL={imgURL} setShowPhotoModal={props.setShowPhotoModal}/>
            </div>
            <div className='profile-box__block-two'></div>
            <div className='profile-box__block-three'></div>

        </div>
    )
}
const mapStateToProps = state => {
    return {
        photoRef: state.photoRef,
    }
}

export default connect(mapStateToProps, null)(ProfileBox)
