        };
    }, [])

    const fetchPhotoUrl = () => {
        var storage = firebase.storage();
        storage.ref(props.photoRef).getDownloadURL().then(url => {
            setPhotoURL(url)
        })
    }

    return (
        <div className='follow-item'>

            <img alt='' src={null}></img>
            {props.username}
        </div>
    )
}

