const PostEditor = (props) => {

    const [postComments, setPostComments] = useState([])
    const [postVotes, setPostVotes] = useState([])

    useEffect(() => {
        const commentsListener = FireApi.submissionCommentsForPostListener(setPostComments, props.pid)
